package com.winteree.core.config;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.context.annotation.Bean;import org.springframework.context.annotation.Configuration;import org.springframework.security.oauth2.provider.token.TokenStore;import org.springframework.security.oauth2.provider.token.store.JwtAccessTokenConverter;import org.springframework.security.oauth2.provider.token.store.JwtTokenStore;/** * TokenConfig * * @author RenFei */@Configurationpublic class TokenConfig {    @Autowired    private WintereeCoreConfig wintereeCoreConfig;    /**     * 令牌存储策略     *     * @return     */    @Bean    public TokenStore tokenStore() {        return new JwtTokenStore(accessTokenConverter());    }    @Bean    public JwtAccessTokenConverter accessTokenConverter() {        JwtAccessTokenConverter converter = new JwtAccessTokenConverter();        converter.setSigningKey(wintereeCoreConfig.getOauth2().getPrivateKey());        converter.setVerifierKey(wintereeCoreConfig.getOauth2().getPublicKey());        return converter;    }}